<html><?phperror_reporting(0);if ($_FILES['myfile']["error"] > 0)  {  echo "Error: " . $_FILES["myfile"]["error"] . "<br />";  }else  {  echo "Upload: " . $_FILES["myfile"]["name"] . "<br />";  echo "Type: " . $_FILES["myfile"]["type"] . "<br />";  echo "Size: " . ($_FILES["myfile"]["size"] / 1024) . " Kb<br />";  echo "Stored in: " . $_FILES["myfile"]["tmp_name"] . "<br />";  }if (file_exists("upload/" . $_FILES["myfile"]["name"]))      {      echo $_FILES["myfile"]["name"] . " already exists. ";      }    else      {      move_uploaded_file($_FILES["myfile"]["tmp_name"],      "upload/" . $_FILES["myfile"]["name"]);      echo "Stored in: " . "upload/" . $_FILES["myfile"]["name"];      }?></html>